import React from "react";

const AwardContnet = [
  {
    img: "1",
    awardName: "Professional Certificate In Data Analytics & Vizualization",
    awardFor: "University of Arizona",
    delayAnimation: "0",
  },
  {
    img: "2",
    awardName: "Professional Certificate in Global Sports Management",
    awardFor: "New York University",
    delayAnimation: "200",
  },
  {
    img: "a3",
    awardName: "Certified Strength & Conditioning Specialist",
    awardFor: "National Strength and Conditioning Association",
    delayAnimation: "400",
  },
];

const Awards = () => {
  return (
    <>
      <div className="row">
        {AwardContnet.map((val, i) => (
          <div
            className="col-lg-4 m-15px-tb"
            key={i}
            data-aos="fade-right"
            data-aos-duration="1200"
            data-aos-delay={val.delayAnimation}
          >
            <div className="feature-box-02 d-flex align-items-center">
              <div className="icon">
                <img src={`img/award/${val.img}.png`} alt="award image" />
              </div>
              <div className="media-body">
                <h6>{val.awardName}</h6>
                <p>{val.awardFor}</p>
              </div>
            </div>
          </div>
          // End .col
        ))}
      </div>
      {/* End .row */}
    </>
  );
};

export default Awards;
